import { Provider } from "react-awesome-slider/dist/navigation";
import Link from 'next/link';
import { useRouter } from "next/router";

// Styles
import 'react-awesome-slider/dist/styles.css';
import "react-awesome-slider/dist/custom-animations/open-animation.css";
import '../static/css/slide-navigator.css';


function App({ Component, pageProps }) {
  let slug = pageProps.navigation
  const router = useRouter();
  return (
    <Provider slug={router.route}>
      <div id="slide-navigator">
        <Link className={slug === "" ? "selected" : null} href="/"><a>Page One</a></Link>
        <Link href="page-two"><a>Page Two</a></Link>
      </div>
      <Component {...pageProps} />
    </Provider>

  );
}

export default App;
